clean_table <- function(df){
  df <- as_tibble(df[[1]],.name_repair = 'minimal')
  names(df) <- c('tmp','cv')
  df <- df %>%
    slice(-(1:3)) %>%
    separate(col = 'tmp',into = c('x', 'ds'),sep = ' ') %>%
    mutate(x = str_remove_all(x, '\\.') %>% as.numeric(),
           ds = str_remove_all(ds, '\\.')%>% as.numeric(),
           cv = str_replace_all(cv, ',','\\.')%>% as.numeric()
    )
}

